import TodoForm from "./TodoForm";
import { useState } from "react";

const DUMMY = [
  { id: "q1", name: "Gbambor", activity: "Football" },
  { id: "q2", name: "Ebuka", activity: "Sells" },
  { id: "q3", name: " Cornelius", activity: "BasketBall" },
  { id: "q4", name: "ElenaMoon", activity: "DrinkBeer" },
];
const Todo = () => {
  const [todo, setTodo] = useState(DUMMY);

  const submitNowHandler = (item) => {
    setTodo((prevState) => {
      return [item, ...prevState];
    });
  };

  const clickHandler = (id) => {
    const newtodo = todo.filter((todo) => todo.id !== id);
    return newtodo;
  };
  return (
    <div>
      <TodoForm onSubmitNow={submitNowHandler} />
      {todo.map((item) => (
        <li id={item.id} key={item.id}>
          <p>{item.name}</p>
          <p>{item.activity}</p>
          <button onClick={clickHandler.bind(null, item.id)}>delete</button>
        </li>
      ))}
    </div>
  );
};

export default Todo;
